import sysimport osimport pytestfrom main import generate_custom_resumesys.path.insert(    0,    os.path.abspath(os.path.join(os.path.dirname(__file__), "../app")))def test_optimize_resume():    """Prueba si la IA devuelve un currículum optimizado en formato Markdown."""    resume_md = """    ## Nombre    John Doe    ## Resumen    Desarrollador Full Stack con 5 años de experiencia.    """    job_description = "Se busca un Desarrollador Backend con experiencia en Python y Django."    optimized_resume = generate_custom_resume(resume_md, job_description)    assert isinstance(optimized_resume, str)    assert "John Doe" in optimized_resume    assert "Python" in optimized_resume or "Django" in optimized_resumedef test_invalid_resume():    """Prueba si la función lanza un error cuando se pasa un currículum vacío."""    with pytest.raises(Exception):        generate_custom_resume("", "Se busca un Backend Developer con Python.")