import sysimport osimport pytestsys.path.insert(0, os.path.abspath(os.path.join(os.path.dirname(__file__), "../app")))from main import generate_custom_resumedef test_optimize_resume():    """Prueba si la IA devuelve un currículum optimizado en formato Markdown."""        resume_md = """    ## Nombre    John Doe    ## Resumen    Desarrollador Full Stack con 5 años de experiencia.    """        job_description = "Se busca un Desarrollador Backend con experiencia en Python y Django."    optimized_resume = generate_custom_resume(resume_md, job_description)    assert isinstance(optimized_resume, str)    assert "John Doe" in optimized_resume    assert "Python" in optimized_resume or "Django" in optimized_resume